package com.freyr.apollo18.commands.utility;

import com.freyr.apollo18.Apollo18;
import com.freyr.apollo18.commands.Category;
import com.freyr.apollo18.commands.Command;
import com.freyr.apollo18.util.embeds.EmbedColor;
import net.dv8tion.jda.api.EmbedBuilder;
import net.dv8tion.jda.api.Permission;
import net.dv8tion.jda.api.events.interaction.command.SlashCommandInteractionEvent;
import net.dv8tion.jda.api.interactions.commands.OptionType;
import net.dv8tion.jda.api.interactions.commands.build.OptionData;

/**
 * This command gives you the invite to invite apollo
 */
public class InviteCommand extends Command {

    public InviteCommand(Apollo18 bot) {
        super(bot);
        this.name = "invite";
        this.description = "Generate an invite to the server or to the bot";
        this.category = Category.UTILITY;
    }

    @Override
    public void execute(SlashCommandInteractionEvent event) {
        event.deferReply().queue();

        EmbedBuilder botEmbed = new EmbedBuilder();
        botEmbed.setTitle("Bot Invite");
        botEmbed.setThumbnail("https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/53653317-d541-48a0-8252-bc533c980e00/ddk20gg-5d876f13-a2e3-4a64-a589-45e1cee6ee40.png/v1/fill/w_600,h_835,strp/apollo___greek_mythology_by_yliade_ddk20gg-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..kJ-P4pwq5NVxQWBqvl_x7s4OQqKyp5GB7202NMCuxP4");
        botEmbed.setColor(EmbedColor.DEFAULT_COLOR);
        botEmbed.setDescription("Thank you for taking an interest in sharing this bot! Apollo is a multi-purpose bot and is currently in development! Important links are below.");
        botEmbed.addField("Server Invite", "Coming soon...", false);
        botEmbed.addField("Bot Invite", "Invite Apollo18 [here](https://discord.com/api/oauth2/authorize?client_id=853812538218381352&permissions=8&scope=applications.commands%20bot)!", true);
        botEmbed.setFooter("Requested by " + event.getUser().getName());
        event.getHook().sendMessageEmbeds(botEmbed.build()).queue();
    }
}
